mapper.update:
    UPDATE FREE_BOARD
		SET 
		  FREE_BOARD_TITLE = #{freeBoardTitle}
		  , FREE_BOARD_CONTENT = #{freeBoardContent}
		  , UPDATE_DATE = SYSDATE
		WHERE FREE_BOARD_ID = #{freeBoardId}
		AND MEMBER_NO = #{memberNo}

mapper.delete:
    DELETE FROM FREE_BOARD_FILE
		WHERE FREE_BOARD_FILE_ID IN 

      #{delFreeBoardFileId}
