mapper.select:
SELECT MEMBER_NO, EMAIL, MEMBER_NAME
FROM MEMBER
WHERE EMAIL = #{email}
AND PWD = #{pwd}

SELECT MEMBER_NO, EMAIL, MEMBER_NAME, CRE_DATE
FROM MEMBER
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE MEMBER
EMAIL = #{email}, MEMBER_NAME = #{memberName}, MOD_DATE = SYSDATE
WHERE MEMBER_NO = #{memberNo}
